			echo json_encode( array( "status" => "ERROR", "message" => "Not allowed to download files" ) );
		elseif( $this->config['showhtdocs'] != 1 && ( substr( $d['filename'], 0, 3 ) == ".ht" || substr( $d['filename'],0,3 ) == ".ht" ) )
